  const [people,setPeople] = useState(data);
  return <section>
    <h2>prop drilling</h2>;
    <List people={people}/>

  </section>
};



const List = ({people}) => {
  return <>
    {people.map((person) => {
      return <singlePerson key={person.id}/>
    })}

  </>
}


const singlePerson = ({id,name}) =>{
  return <div className = 'item'>
